import React, { useEffect } from 'react';
import Idea from './Idea';

function IdeaList () {

  const [ideas, deleteIdea, editIdea, setIdeas] = useState([]) 

  useEffect(() => {
    fetch("http://localhost:8000/ideas")
    .then(r => r.json())
    .then(data => setIdeas)
  })

  return (
    <div>
      {ideas && ideas.map((idea) => {
        return(

        <Idea

          key={idea.id} 
          idea={idea} 
          deleteIdea={deleteIdea} 
          editIdea={editIdea}
          />

        );
      })}
    </div>
  );
};


export default IdeaList;
