import React, { useState, useEffect } from 'react';import Idea from './Idea';

const IdeaList = ({ideas, deleteIdea, editIdea}) => {
  const [setIdeas] = useState([]);

  useEffect(() => {
    fetchIdeas();
  }, []);

  const fetchIdeas = async () => {
    try {
      const response = await fetch('http://localhost:3001/ideas');
      const data = await response.json();
      setIdeas(data);
    } catch (error) {
      console.error('Error fetching ideas:', error);
    }
  };

  return (
    <div>
      {ideas && ideas.map((idea) => {
        return(

        <Idea

          key={idea.id} 
          idea={idea} 
          deleteIdea={deleteIdea} 
          editIdea={editIdea}
          />

        );
      })}
    </div>
  );
};

export default IdeaList;
